INSERT INTO `osdate_languages` VALUES(1290, 'english', 'profile_confirmation_email', 'text', 'Dear #FirstName#,\r\n\r\nThank you for registering with #siteName#! As the newest member of our community, I encourage you to explore our services and features.\r\n\r\nTo confirm your profile addition, please click the link below. Or, if the link is not clickable, copy and paste it into address bar of your web browser, to directly access it.\r\n\r\n#ConfirmationLink#=#ConfCode#\r\n\r\nIf you still have the final step of the registration wizard open, you can input your confirmation code on that screen.\r\n\r\nYour confirmation code is: #ConfCode#\r\n\r\nWe have recorded the following registration information for you:\r\n\r\nUsername: #StrID#\r\nPassword: #Password#\r\nE-Mail: #Email#\r\n\r\nPlease keep this information in a safe, secure place, so that you will be able to access all of the services and features available to you. Some services may require an upgrade to a higher membership level, which you can do here:\r\n\r\n#SiteUrl#payment.php\r\n\r\nThanks again for using our services, and we hope that you find your match!\r\n\r\n#AdminName#\r\n#siteName#');
