INSERT INTO `osdate_languages` VALUES(3142, 'portuguese', 'message_read', 'html', '&lt;table border=0 cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; width=&quot;570&quot;&gt;&lt;tr&gt;&lt;td height=&quot;25&quot; width=&quot;100%&quot;&gt;&lt;div class=&quot;module_head&quot;&gt;&lt;table border=0 cellspacing=0 cellpadding=0 width=&quot;100%&quot;&gt;&lt;tr&gt;&lt;td width=&quot;77&quot; height=&quot;25&quot;&gt;\r\n&lt;div class=&quot;module_head&quot;&gt;#email_hdr_left#&lt;/div&gt;&lt;/td&gt;&lt;td width=&quot;493&quot; &gt;&lt;div class=&quot;module_head&quot;&gt;&amp;nbsp;&amp;nbsp;A sua mensagem para #RecipientName# foi lida! &lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;\r\n&lt;tr&gt;&lt;td width=&quot;100%&quot; class=&quot;evenrow&quot; colspan=&quot;2&quot; &gt;\r\n&lt;div class=&quot;evenrow&quot; &gt;&lt;table border=&quot;0&quot; cellspacing=&quot;0&quot; cellpadding=&quot;5&quot;&gt;&lt;tr&gt;&lt;td height=&quot;2&quot;&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;50%&quot; valign=&quot;top&quot; class=&quot;evenrow&quot;&gt;Caro #FirstName#,&lt;br&gt;&lt;br&gt;a mensagem que enviou para &lt;b&gt;#RecipientName#&lt;/b&gt; foi lida.&lt;br&gt;&lt;br&gt;Para ver o perfil deste utilizador visite &lt;a href=\\&quot;#link#\\&quot;&gt;SITENAME&lt;/a&gt;.&lt;br&gt;&lt;br&gt;Boa Sorte!&lt;br&gt;#AdminName# &lt;br&gt;SITENAME&lt;br&gt;&lt;/td&gt;\r\n&lt;td valign=&quot;top&quot;&gt;#smallProfile#&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;\r\n&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td height=&quot;6&quot; colspan=&quot;2&quot; class=&quot;evenrow&quot;&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt; ');
INSERT INTO `osdate_languages` VALUES(3143, 'portuguese', 'featured_profile_added', 'text', 'Caro(a) #FirstName#,\r\n\r\n&amp;Eacute; de nosso inteiro prazer que incluimos o seu perfil na Lista de Perfis em Destaque em &lt;a href=&quot;#link#&quot;&gt;#siteName#&lt;/a&gt;.\r\n\r\nO seu perfil estar&amp;aacute; em Destaque de #FromDate# a #UptoDate#.\r\n\r\nCom isto ir&amp;atilde;o aumentar as visualiza&amp;ccedil;&amp;otilde;es do seu perfil e pode resultar em v&amp;aacute;rias visualiza&amp;ccedil;&amp;otilde;es de potenciais pares.\r\n\r\nBoa sorte!\r\n#AdminName#\r\nSITENAME');
INSERT INTO `osdate_languages` VALUES(3144, 'portuguese', 'featured_profile_added', 'html', '&lt;table border=0 cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; width=&quot;570&quot;&gt;&lt;tr&gt;&lt;td height=&quot;25&quot; width=&quot;100%&quot;&gt;&lt;div class=&quot;module_head&quot;&gt;&lt;table border=0 cellspacing=0 cellpadding=0 width=&quot;100%&quot;&gt;&lt;tr&gt;\r\n&lt;td width=&quot;77&quot; height=&quot;25&quot; &gt;&lt;div class=&quot;module_head&quot;&gt;#email_hdr_left#&lt;/div&gt;\r\n&lt;/td&gt;&lt;td width=&quot;493&quot; &gt;&lt;div class=&quot;module_head&quot;&gt;&amp;nbsp;&amp;nbsp;O seu perfil ser&amp;aacute; colocado em destaque brevemente &lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=&quot;100%&quot; class=&quot;evenrow&quot; colspan=&quot;2&quot; &gt;\r\n&lt;div class=&quot;evenrow&quot;&gt;&lt;table border=&quot;0&quot; cellspacing=&quot;0&quot; cellpadding=&quot;5&quot;&gt;&lt;tr&gt;&lt;td width=&quot;100%&quot; valign=&quot;top&quot; class=&quot;evenrow&quot;&gt;Caro #FirstName#,&lt;br&gt;&lt;br&gt;\r\n&amp;Eacute; com enorme prazer que incluimos o seu perfil na Lista de Perfis em Destaque do &lt;a href=\\&quot;#link#\\&quot;&gt;SITENAME&lt;/a&gt;.&lt;br&gt;&lt;br&gt;O ser perfil estar&amp;aacute; em destaque de &lt;b&gt;#FromDate#&lt;/b&gt; a &lt;b&gt;#UptoDate#&lt;/b&gt;.&lt;br&gt;\r\n&lt;br&gt;Isto ir&amp;aacute; fazer com que aumente a visibilidade do seu perfil e poder&amp;aacute; resultar em muitas visualiza&amp;ccedil;&amp;otilde;es de potenciais pares.&lt;br&gt;&lt;br&gt;Boa Sorte!&lt;br&gt;#AdminName# &lt;br&gt;SITENAME&lt;br&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td height=&quot;6&quot; colspan=&quot;2&quot; class=&quot;evenrow&quot;&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt; ');
INSERT INTO `osdate_languages` VALUES(3145, 'portuguese', 'wink_received', 'text', 'Caro(a) #FirstName#,\r\n\r\nRecebeu uma piscadela de #siteName# utilizador ''#SenderName#''.\r\n\r\nVisite &lt;a href=&quot;#link#&quot;&gt;#siteName#&lt;/a&gt; para mandar ''#SenderName#'' uma mensagem, ou retribuir a piscadela.\r\n\r\nBoa sorte!\r\n#AdminName#\r\nSITENAME');
